import React from "react";
import YouTube from "react-youtube";
import { useState, useEffect } from "react";
import axios from "../../utils/axios";
import "./MovieDetail.css";

const opts = {
  height: "400",
  width: "100%",
  playerVars: {
    autoplay: 0,
  },
};

const MovieDetail = ({ movieData }) => {
  const { id, release_date, title, name, overview, vote_average } = movieData;
  useEffect(() => {
	console.log(id);
	  async function fetchTrailer() {
		await axios
		  .post("http://localhost:3001/api/movies/video", {
			filmId: id,
		  })
		  .then((data) => console.log('đâsdasdasd',data))

	  }
	  fetchTrailer();
	  console.log(id);
  })

  return (
    <div className="movie_detail">
      <div className="movie_detail_data">
        <h1>{title || name}</h1>
        <hr></hr>

        <h3>Release Date: {release_date}</h3>
        <h3>Vote: {vote_average} / 10</h3>
        <br></br>
        <p>{overview}</p>
      </div>
      <div className="movie_detail_trailer">
        <YouTube key="vc7_mH2PWHs" opts={opts} />
        <iframe
          width="100%"
          height="400"
          src="https://www.youtube.com/embed/GD8nCSr54PA"></iframe>
      </div>
    </div>
  );
};

export default MovieDetail;
